import React, { useEffect, useState } from "react";

const headings = [
  {
    title: "Yoga",
    color: "text-[#fca311]", // orange
    image: "https://www.financialexpress.com/wp-content/uploads/2022/06/modi-yoga1.jpeg", // Yoga image
  },
  {
    title: "Meditation",
    color: "text-[#fca311]",
    image: "data:image/jpeg;base64,", // Meditation image
  },
  {
    title: "Breathwork",
    color: "text-[#fca311]",
    image: "https://www.hindustantimes.com/ht-img/img/2023/06/21/1600x900/narendra-modi-Yoga_1687347314338_1687347328015.jpg", // Breathwork image
  },
  {
    title: "Ayurveda",
    color: "text-[#fca311]",
    image: "data:image/jpeg;base64,", // Ayurveda image
  },
];

function Hero() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % headings.length);
    }, 3000); // 3 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="relative w-full min-h-screen bg-cover bg-center flex flex-col items-center justify-center px-6"
      style={{
        backgroundImage: `url('https://images.unsplash.com/photo-1506744038136-46273834b3fb')`,
      }}
    >
      <div className="text-center z-10">
        <h1 className="text-4xl md:text-5xl font-bold text-gray-900">
          Create <em>Your</em> Daily Ritual
        </h1>
        <p className="mt-4 text-xl md:text-2xl font-medium space-x-2">
          {headings.map((item, index) => (
            <span key={index} className={`${index === currentIndex ? item.color : "text-gray-500"} transition-all duration-300`}>
              {item.title}
              {index < headings.length - 1 && <span className="text-gray-400"> / </span>}
            </span>
          ))}
        </p>
      </div>

      {/* Rotating Image */}
      <div className="mt-12 z-10">
        <div className="w-72 h-72 md:w-96 md:h-96 rounded-full overflow-hidden bg-white shadow-xl flex items-center justify-center transition-all duration-500">
          <img
            src={headings[currentIndex].image}
            alt={headings[currentIndex].title}
            className="w-full h-full object-cover"
          />
        </div>
      </div>

      {/* Optional overlay or gradient for better text visibility */}
      <div className="absolute inset-0 bg-white opacity-30"></div>
    </div>
  );
}

export default Hero;
